require './lib/helpers/gkelite/gk_header'


class GKShopifyBasePage < BasePage
	include PageObject
	include GKHeader

	text_field(:password, id: "password")
	button(:enter, name: "commit")

	def get_spo_product_handles
		@handles = Array.new
		options = AssetAPI.scene_productoptions_keys('gk-prs-gym')
		options.flatten
		options.each do |opts|
			@handles.push(opts)
		end
		@handles.uniq!
		@handles.reject! { |r| r.include?("extra" || "mannequin" || "scene.json" || "dummy") }
		@handles.flatten!
	end


	def enter_password
		self.password_element.send_keys "iawhao"
		self.enter_element.click
	end


	def login_page
		$driver.goto $base_url + "/account/login"
		return GKLoginPage.new
	end

	def cart_page
		$driver.goto $base_url + "/cart"
		return GKCartPage.new
	end

	def search_page
		$driver.goto $base_url + "/pages/search-results-page?q="
		return GKSearchPage.new
	end

	def product_page(id)
		$driver.goto $base_url + "/products/#{id}"
		return GKProductPage.new
	end

	def home_page
		$driver.goto $base_url
		return GKHomePage.new
	end

	def collections(id)
		$driver.goto $base_url + "/collections/#{id}?view=product"
	end

	def spec_data(valueGroup)
		case ENV['ENVIRONMENT'].to_sym
		when :test then @data = JSON.parse(RestClient.get("https://test.spectrumcustomizer.com/api/settings/valuegroup/#{valueGroup}"))
		when :staging then @data = JSON.parse(RestClient.get("https://staging.spectrumcustomizer.com/api/settings/valuegroup/#{valueGroup}"))
		when :prod then @data = JSON.parse(RestClient.get("https://api.spectrumcustomizer.com/api/settings/valuegroup/#{valueGroup}"))
		end
		@hash = Hash.new{ |hsh,key| hsh[key] = [] }
		@data['valueMaps'].each do |vm|
			@hash.store(vm['inputValue'],vm['outputValueFormat'])
		end
		return @hash
	end

